mapper.select:
select
  id, nickname, mobile, login_name, email, area_code, avatar, valid, status, created_at, gender, zip, version, outid, password
from is_users


        and id = #{id}


        and nickname = #{nickname}


        and mobile = #{mobile}


        and login_name = #{loginName}


        and email = #{email}


        and area_code = #{areaCode}


        and avatar = #{avatar}


        and valid = #{valid}


        and status = #{status}


        and created_at = #{createdAt}


        and gender = #{gender}


        and zip = #{zip}


        and version = #{version}


        and outid = #{outid}


        and password = #{password}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from is_users


        and id = #{id}


        and nickname = #{nickname}


        and mobile = #{mobile}


        and login_name = #{loginName}


        and email = #{email}


        and area_code = #{areaCode}


        and avatar = #{avatar}


        and valid = #{valid}


        and status = #{status}


        and created_at = #{createdAt}


        and gender = #{gender}


        and zip = #{zip}


        and version = #{version}


        and outid = #{outid}


        and password = #{password}
